/*
XBlack
A minimalistic, black theme
STUDIOXENIX
1
*/
body {
	background: black;
}

#content {
	background: black !important;	
}

#right_column {
	background: none !important;
	color: black;	
}

.notification,
.controls_section,
.controls_section li {
	background-color: black;
	box-shadow: none;
	border: 0;	
}

.controls_section * {
	box-shadow: none !important;	
}

.controls_section li:hover {
	background-color: rgb(40,40,40);	
}

.controls_section li {
	border: 0;	
}

.selection_nipple {
	display: none !important;	
}

#right_column:after {
	display: none !important;		
}

.notification .notification_right .preview_frame {
	border: 1px solid rgb(50,50,50);	
}

#header *,
#content * {
	border-radius: 0px;	
}

.xoldeheader-item {
	border-radius: 7px !important;	
}

/*
OrangeWall
"That is one orange wallpaper you got there."
STUDIOXENIX
1
*/
body {
	background: #f49a00  url('http://www.studioxenix.com/seven/themes/themes/orange_wall/index.gif');
}

.selection_nipple, .permalink {
	visibility: hidden !important; 
}
.recessed {
	background: rgba(0,0,0,0.25) !important; 
	opacity: 0.8 !important; 
	-webkit-transition: 0.3s ease-in; 
	-moz-transition: 0.3s ease-in; 
	-o-transition: 0.3s ease-in; 
	transition: 0.3s ease-in; 
}
.controls_section {
	box-shadow: 0px -1px 0px 0px rgba(0, 0, 0, 0.09) !important; 
	border: 1px solid rgba(0,0,0,0.25) !important; 
	background: rgba(20,40,70,0.25) !important; 
	opacity: 1 !important; 
}
#header {
	opacity: 0.8 !important; 
	-webkit-transition: 0.4s ease-in; 
	-moz-transition: 0.4s ease-in; 
	-o-transition: 0.4s ease-in; 
	transition: 0.4s ease-in; 
}
#logo {
	opacity: 0.5 !important; 
	-webkit-transition: 0.4s ease-in; 
	-moz-transition: 0.4s ease-in; 
	-o-transition: 0.4s ease-in; 
	transition: 0.4s ease-in; 
}
#header:hover, #logo:hover {
	opacity: 1 !important; 
	-webkit-transition: 0.4s ease-in; 
	-moz-transition: 0.4s ease-in; 
	-o-transition: 0.4s ease-in; 
	transition: 0.4s ease-in; 
}
a {
	-webkit-transition: 0.4s ease-in; 
	-moz-transition: 0.4s ease-in; 
	-o-transition: 0.4s ease-in; 
	transition: 0.4s ease-in; 
}
ul.controls_section li {
	text-shadow: 0px -1px 0px 0px rgba(0, 0, 0, 0.12) !important; 
	-webkit-transition: 0.3s ease-in; 
	-moz-transition: 0.3s ease-in; 
	-o-transition: 0.3s ease-in; 
	transition: 0.3s ease-in; 
}
ul.controls_section li:hover {
	backgrjound: rgba(20,40,70,0.35) !important; 
	-webkit-transition: 0.3s ease-in; 
	-moz-transition: 0.3s ease-in; 
	-o-transition: 0.3s ease-in; 
	transition: 0.3s ease-in; 
}

li.post {
	box-shadow: 0px -1px 0px 0px rgba(0, 0, 0, 0.22) !important; 
	opacity: 0.99 !important; 
	-webkit-transition: 0.3s ease-in; 
	-moz-transition: 0.3s ease-in; 
	-o-transition: 0.3s ease-in; 
	transition: 0.3s ease-in; 
}

li.post:hover {
	opacity: 1 !important; 
	-webkit-transition: 0.5s ease-in; 
	-moz-transition: 0.5s ease-in; 
	-o-transition: 0.5s ease-in; 
	transition: 0.5s ease-in; 
}

#content {
	background: transparent !important;
	margin-top: 8px !important; 
	padding-top: 0 !important; 
}

#right_column:after {
	display: none !important;		
}

#header *,
#content * {
	border-radius: 0px;	
}

.xoldeheader-item {
	border-radius: 7px !important;	
}

